      "source": [
        "import pandas as pd\n",
        "import numpy as np\n",
        "import matplotlib.pyplot as plt\n",
        "from sklearn.linear_model import LinearRegression\n",
        "\n",
        "#creating a sample dataset\n",
        "data={\n",
        "    \"Size\": [800,1000,1200,1500,1800],\n",
        "    \"Price\": [150,200,240,300,350]\n",
        "}\n",
        "\n",
        "#converting the data into a dataframe\n",
        "df=pd.DataFrame(data)\n",
        "\n",
        "#seperating the feature X and target Y\n",
        "X=df[[\"Size\"]]\n",
        "y=df[[\"Price\"]]\n",
        "\n",
        "#create and train linear regression model\n",
        "model = LinearRegression()\n",
        "model.fit(X,y)\n",
        "\n",
        "#predicting the price of a house with size 1000\n",
        "predicted_price=model.predict([[1650]])\n",
        "\n",
        "#print model details\n",
        "print(\"Slope(coefficient):\",model.coef_[0])\n",
        "print(\"Intercept:\", model.intercept_)\n",
        "print(\"predicted price for 1650 sq.ft.:\",predicted_price[0])\n",
        "\n",
        "#plotting the graph\n",
        "plt.scatter(X,y,color='Blue', label=\"Actual Data\")\n",
        "plt.plot(X,model.predict(X),color='Red',label=\"model\")\n",
        "plt.xlabel(\"Size\")\n",
        "plt.ylabel(\"Price\")\n",
        "plt.legend()\n",
        "plt.show()"
      ]
